import { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [file, setFile] = useState(null);
  const [uploadStatus, setUploadStatus] = useState('');
  const [requirements, setRequirements] = useState(null);
  const [testResults, setTestResults] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [progress, setProgress] = useState('');

  const ALLOWED_TYPES = [
    'application/pdf',
    'application/vnd.openxmlformats-officedocument.wordprocessingml.document'
  ];

  useEffect(() => {
    const ws = new WebSocket(`ws://${window.location.hostname}:5000`);
    ws.onmessage = (e) => {
      const data = JSON.parse(e.data);
      if (data.type === 'progress') {
        setProgress(data.message);
      }
    };
    return () => ws.close();
  }, []);

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (!selectedFile) return;

    setRequirements(null);
    setTestResults(null);
    setUploadStatus('');
    setProgress('');

    if (!ALLOWED_TYPES.includes(selectedFile.type)) {
      setUploadStatus('Error: Only PDF and DOCX files are allowed');
      return;
    }

    if (selectedFile.size > 5 * 1024 * 1024) {
      setUploadStatus('Error: File size must be less than 5MB');
      return;
    }

    setFile(selectedFile);
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!file) {
      setUploadStatus('Please select a valid file first');
      return;
    }

    const formData = new FormData();
    formData.append('srsDocument', file);

    try {
      setIsLoading(true);
      setUploadStatus('Uploading and analyzing document...');
      setProgress('Starting analysis...');

      await axios.get(`http://${window.location.hostname}:5000/api/health`, { timeout: 5000 });

      const res = await axios.post(`http://${window.location.hostname}:5000/api/upload`, formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
        timeout: 30000
      });

      setRequirements(res.data);
      setUploadStatus(
        res.data.analysisStatus === 'failed'
          ? 'Analysis completed with warnings'
          : 'Analysis complete!'
      );
      setProgress('');
    } catch (err) {
      let errorMsg = 'Processing failed. Please try again.';
      let details = '';

      if (err.code === 'ECONNABORTED') {
        errorMsg = 'Request timeout (30s) exceeded';
        details = 'Backend took too long to respond';
      } else if (err.response) {
        details = err.response.data.details || '';
        errorMsg = err.response.data.error || errorMsg;
      } else if (err.request) {
        details = 'Cannot connect to backend server';
      } else {
        details = err.message;
      }

      setUploadStatus(errorMsg);
      setTestResults({ error: errorMsg, details });
      setProgress('');
    } finally {
      setIsLoading(false);
    }
  };

  const runTests = async () => {
    if (!requirements?.testScript) return;

    try {
      setIsLoading(true);
      setUploadStatus('Running tests...');
      setTestResults(null);
      setProgress('Initializing test environment...');

      await axios.get(`http://${window.location.hostname}:5000/api/health`, { timeout: 5000 });

      const res = await axios.post(`http://${window.location.hostname}:5000/api/run-tests`, {
        testScript: requirements.testScript
      }, {
        timeout: 120000,
        headers: { 'Content-Type': 'application/json' }
      });

      setTestResults(res.data);
      setUploadStatus(res.data.passed ? '‚úì Tests passed!' : '‚úó Tests failed');
      setProgress('');

      if (res.data.passed !== undefined) {
        const reportRes = await axios.get(`http://${window.location.hostname}:5000/api/report/${Date.now()}`, {
          responseType: 'blob'
        });
        const url = window.URL.createObjectURL(new Blob([reportRes.data]));
        const link = document.createElement('a');
        link.href = url;
        link.setAttribute('download', `QA_Report_${new Date().toISOString()}.pdf`);
        document.body.appendChild(link);
        link.click();
      }

    } catch (err) {
      let errorMsg = 'Test execution failed';
      let details = '';

      if (err.code === 'ECONNABORTED') {
        errorMsg = 'Test timeout (120s) exceeded';
        details = 'Tests took too long to execute';
      } else if (err.response) {
        details = err.response.data.details || '';
        errorMsg = err.response.data.error || errorMsg;
      } else if (err.request) {
        details = 'Backend connection failed';
      } else {
        details = err.message;
      }

      setUploadStatus(errorMsg);
      setTestResults({
        error: errorMsg,
        details,
        logs: err.response?.data?.logs || ''
      });
      setProgress('');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 p-4 md:p-8">
      <div className="max-w-5xl mx-auto space-y-6">
        <h1 className="text-3xl font-bold text-blue-700">AUTOMATED QA SYSTEM USING AI AGENTS AND PLAYWRIGHT</h1>

        {/* Informational Section */}
        <div className="bg-white p-6 rounded-lg shadow-md space-y-4">
          <section>
            <h2 className="text-xl font-semibold text-gray-800">QA - Quality Assurance</h2>
            <p className="text-gray-700 mt-1">
              QA ensures that software meets specified requirements and functions as expected without bugs or errors.
              It involves both manual and automated testing.
            </p>
          </section>
          <section>
            <h2 className="text-xl font-semibold text-gray-800">AI Agent Responsibilities</h2>
            <ul className="list-disc pl-6 text-gray-700 space-y-1">
              <li>üìñ Read and analyze the SRS document</li>
              <li>üß™ Generate test cases to validate system behavior</li>
              <li>‚öôÔ∏è Automate testing by converting test cases into Playwright scripts</li>
              <li>üìã Generate reports summarizing test results and failures</li>
            </ul>
          </section>
          <section>
            <h2 className="text-xl font-semibold text-gray-800">SRS (Software Requirements Specification)</h2>
            <p className="text-gray-700 mt-1">
              The SRS outlines the desired features, functionalities, and constraints of a software application.
              It forms the foundation for creating test cases as it clearly defines what needs to be validated.
            </p>
          </section>
          <section>
            <h2 className="text-xl font-semibold text-gray-800">Playwright</h2>
            <p className="text-gray-700 mt-1">
              Playwright is an open-source automation framework for web application testing. It supports:
            </p>
            <ul className="list-disc pl-6 text-gray-700">
              <li>‚úÖ Cross-browser testing</li>
              <li>‚úÖ Cross-platform testing</li>
            </ul>
          </section>
        </div>

        {/* Existing Upload & Test UI */}
        {/* The rest of your code follows here (unchanged)... */}
        {/* From your upload form to test results display */}
        {/* Already included above */}
      </div>
    </div>
  );
}

export default App;
